('e2', 'bass', 4, 'b1'),
('e3', 'lead', 3, 'b1'),
('e2', 'bass', 4, 'b1'),
('e4', 'drums', 8, 'b1');

-- Verify ledger contents
SELECT 'LEDGER:' AS section;
SELECT event_id, category, amount, batch_id, checkpoint_tag FROM ledger ORDER BY event_id;

-- Verify rejects contents
SELECT 'REJECTS:' AS section;
SELECT event_id, reason FROM rejects ORDER BY event_id;

-- Verify checkpoint_log contents
SELECT 'CHECKPOINT_LOG:' AS section;
SELECT checkpoint_tag, row_count FROM checkpoint_log;

-- Final deterministic aggregate query
SELECT 'AGGREGATE TOTALS:' AS section;
SELECT category, SUM(amount) AS total FROM ledger GROUP BY category ORDER BY category;
