tool_input.sql:
-- Bootstrap schema
DROP TABLE IF EXISTS ledger;
DROP TABLE IF EXISTS rejects;
DROP TABLE IF EXISTS checkpoint_log;
DROP TABLE IF EXISTS fixture_seed;

CREATE TABLE ledger (
  event_id TEXT PRIMARY KEY,
  category TEXT NOT NULL,
  amount INTEGER NOT NULL,
  batch_id TEXT NOT NULL,
  checkpoint_tag TEXT NOT NULL
);

CREATE TABLE rejects (
  event_id TEXT NOT NULL,
  reason TEXT NOT NULL
);

CREATE TABLE checkpoint_log (
  checkpoint_tag TEXT PRIMARY KEY,
  row_count INTEGER NOT NULL
);

-- Load fixture data
CREATE TABLE fixture_seed (
  event_id TEXT NOT NULL,
  category TEXT NOT NULL,
  amount INTEGER NOT NULL,
  batch_id TEXT NOT NULL
);

INSERT INTO fixture_seed (event_id, category, amount, batch_id) VALUES
('e1', 'drums', 5, 'b1'),
('e2', 'bass', 4, 'b1'),
('e3', 'lead', 3, 'b1'),
('e2', 'bass', 4, 'b1'),
('e4', 'drums', 8, 'b1');

-- Start transaction
BEGIN TRANSACTION;

-- Step 1: Insert first-seen rows into ledger (INSERT OR IGNORE to handle duplicates)
INSERT OR IGNORE INTO ledger (event_id, category, amount, batch_id, checkpoint_tag)
SELECT event_id, category, amount, batch_id, 'CKP-APR-01'
FROM fixture_seed;

-- Step 2: Insert duplicate rows into rejects
-- Identify duplicates: rows that were NOT inserted (already exist in ledger)
INSERT INTO rejects (event_id, reason)
SELECT event_id, 'duplicate_event'
FROM fixture_seed
WHERE event_id NOT IN (
  SELECT DISTINCT event_id FROM fixture_seed
  WHERE (event_id, rowid) IN (
    SELECT event_id, MIN(rowid) FROM fixture_seed GROUP BY event_id
  )
);

-- Step 3: Upsert checkpoint_log with total row count from fixture
INSERT INTO checkpoint_log (checkpoint_tag, row_count)
VALUES ('CKP-APR-01', (SELECT COUNT(*) FROM fixture_seed))
ON CONFLICT(checkpoint_tag) DO UPDATE SET row_count = excluded.row_count;

-- Commit transaction
COMMIT;

-- Verify: Return aggregate totals by category
SELECT category, SUM(amount) AS total 
FROM ledger 
GROUP BY category 
ORDER BY category;

-- Verify ledger contents
SELECT 'LEDGER:' AS table_name;
SELECT * FROM ledger ORDER BY event_id;

-- Verify rejects contents
SELECT '' AS spacer;
SELECT 'REJECTS:' AS table_name;
SELECT * FROM rejects ORDER BY event_id;

-- Verify checkpoint_log contents
SELECT '' AS spacer;
SELECT 'CHECKPOINT_LOG:' AS table_name;
SELECT * FROM checkpoint_log;

-- Verify counts
SELECT '' AS spacer;
SELECT 'SUMMARY:' AS section;
SELECT COUNT(*) AS ledger_count FROM ledger;
SELECT COUNT(*) AS rejects_count FROM rejects;

-- Reset and redo with correct duplicate detection
DROP TABLE IF EXISTS ledger;
DROP TABLE IF EXISTS rejects;
DROP TABLE IF EXISTS checkpoint_log;

CREATE TABLE ledger (
  event_id TEXT PRIMARY KEY,
  category TEXT NOT NULL,
  amount INTEGER NOT NULL,
  batch_id TEXT NOT NULL,
  checkpoint_tag TEXT NOT NULL
);

CREATE TABLE rejects (
  event_id TEXT NOT NULL,
  reason TEXT NOT NULL
);

CREATE TABLE checkpoint_log (
  checkpoint_tag TEXT PRIMARY KEY,
  row_count INTEGER NOT NULL
);

-- Start transaction
BEGIN TRANSACTION;

-- Step 1: Insert first-seen rows into ledger
-- Use ROW_NUMBER to identify which rows are duplicates
INSERT INTO ledger (event_id, category, amount, batch_id, checkpoint_tag)
SELECT event_id, category, amount, batch_id, 'CKP-APR-01'
FROM (
  SELECT event_id, category, amount, batch_id,
         ROW_NUMBER() OVER (PARTITION BY event_id ORDER BY rowid) AS rn
  FROM fixture_seed
) t
WHERE rn = 1;

-- Step 2: Insert duplicate rows into rejects (rows where rn > 1)
INSERT INTO rejects (event_id, reason)
SELECT event_id, 'duplicate_event'
FROM (
  SELECT event_id, category, amount, batch_id,
         ROW_NUMBER() OVER (PARTITION BY event_id ORDER BY rowid) AS rn
  FROM fixture_seed
) t
WHERE rn > 1;

-- Step 3: Log checkpoint with total fixture row count
INSERT INTO checkpoint_log (checkpoint_tag, row_count)
VALUES ('CKP-APR-01', (SELECT COUNT(*) FROM fixture_seed))
ON CONFLICT(checkpoint_tag) DO UPDATE SET row_count = excluded.row_count;

-- Commit transaction
COMMIT;

-- Verify: Return aggregate totals by category
SELECT category, SUM(amount) AS total 
FROM ledger 
GROUP BY category 
ORDER BY category;

-- Comprehensive verification
SELECT 'LEDGER (4 unique by event_id):' AS section;
SELECT * FROM ledger ORDER BY event_id;

SELECT '' AS spacer;
SELECT 'REJECTS (duplicates with reason):' AS section;
SELECT * FROM rejects ORDER BY event_id;

SELECT '' AS spacer;
SELECT 'CHECKPOINT_LOG (5 total fixture rows):' AS section;
SELECT * FROM checkpoint_log;

SELECT '' AS spacer;
SELECT 'AGGREGATE TOTALS:' AS section;
SELECT category, SUM(amount) AS total 
FROM ledger 
GROUP BY category 
ORDER BY category;
