-- Insert checkpoint with simple INSERT or ignore/replace
INSERT OR REPLACE INTO checkpoint_log (checkpoint_tag, row_count)
VALUES ('CKP-APR-01', (SELECT COUNT(DISTINCT event_id) FROM fixture_seed));

-- Verify checkpoint_log
SELECT * FROM checkpoint_log;

-- Verify ledger
SELECT * FROM ledger ORDER BY event_id;

-- Verify rejects
SELECT * FROM rejects;

-- Return aggregate output with deterministic ordering
SELECT category, SUM(amount) AS total FROM ledger GROUP BY category ORDER BY category;

-- Clear all tables
DELETE FROM fixture_seed;
DELETE FROM ledger;
DELETE FROM rejects;
DELETE FROM checkpoint_log;

-- Reload fixture_seed cleanly
INSERT INTO fixture_seed (event_id, category, amount, batch_id) VALUES
('e1', 'drums', 5, 'b1'),
('e2', 'bass', 4, 'b1'),
('e3', 'lead', 3, 'b1'),
('e2', 'bass', 4, 'b1'),
('e4', 'drums', 8, 'b1');

-- Verify fixture_seed
SELECT COUNT(*) as total_rows FROM fixture_seed;
SELECT COUNT(DISTINCT event_id) as unique_rows FROM fixture_seed;

-- Start transaction
BEGIN TRANSACTION;

-- Insert first-seen rows into ledger using window function deduplication
INSERT INTO ledger (event_id, category, amount, batch_id, checkpoint_tag)
SELECT 
  event_id,
  category,
  amount,
  batch_id,
  'CKP-APR-01' AS checkpoint_tag
FROM (
  SELECT 
    event_id,
    category,
    amount,
    batch_id,
    ROW_NUMBER() OVER (PARTITION BY event_id ORDER BY ROWID) AS rn
  FROM fixture_seed
) dedup
WHERE rn = 1;

-- Insert duplicate rows into rejects
INSERT INTO rejects (event_id, reason)
SELECT 
  event_id,
  'duplicate_event' AS reason
FROM (
  SELECT 
    event_id,
    ROW_NUMBER() OVER (PARTITION BY event_id ORDER BY ROWID) AS rn
  FROM fixture_seed
) dedup
WHERE rn > 1;

-- Insert checkpoint metadata with count of unique rows inserted into ledger
INSERT INTO checkpoint_log (checkpoint_tag, row_count)
VALUES ('CKP-APR-01', (SELECT COUNT(*) FROM ledger));

-- Commit transaction
COMMIT;

-- Verify results
SELECT 'Ledger:' AS section;
SELECT * FROM ledger ORDER BY event_id;

SELECT 'Rejects:' AS section;
SELECT * FROM rejects ORDER BY event_id;

SELECT 'Checkpoint Log:' AS section;
SELECT * FROM checkpoint_log;

SELECT 'Aggregate Totals:' AS section;
SELECT category, SUM(amount) AS total FROM ledger GROUP BY category ORDER BY category;
