tool_input.sql:
-- First, ensure tables are created from bootstrap
DROP TABLE IF EXISTS ledger;
DROP TABLE IF EXISTS rejects;
DROP TABLE IF EXISTS checkpoint_log;

CREATE TABLE ledger (
  event_id TEXT PRIMARY KEY,
  category TEXT NOT NULL,
  amount INTEGER NOT NULL,
  batch_id TEXT NOT NULL,
  checkpoint_tag TEXT NOT NULL
);

CREATE TABLE rejects (
  event_id TEXT NOT NULL,
  reason TEXT NOT NULL
);

CREATE TABLE checkpoint_log (
  checkpoint_tag TEXT PRIMARY KEY,
  row_count INTEGER NOT NULL
);

-- Import fixture into temporary table for processing
CREATE TEMPORARY TABLE fixture_seed (
  event_id TEXT NOT NULL,
  category TEXT NOT NULL,
  amount INTEGER NOT NULL,
  batch_id TEXT NOT NULL
);

INSERT INTO fixture_seed VALUES
('e1','drums',5,'b1'),
('e2','bass',4,'b1'),
('e3','lead',3,'b1'),
('e2','bass',4,'b1'),
('e4','drums',8,'b1');

-- Begin transaction for atomicity
BEGIN TRANSACTION;

-- Step 1: Insert first-seen rows (rn=1) into ledger using conflict resolution
INSERT INTO ledger (event_id, category, amount, batch_id, checkpoint_tag)
SELECT 
  event_id, 
  category, 
  amount, 
  batch_id,
  'CKP-APR-01' as checkpoint_tag
FROM (
  SELECT 
    *,
    ROW_NUMBER() OVER (PARTITION BY event_id ORDER BY ROWID) as rn
  FROM fixture_seed
) t
WHERE rn = 1;

-- Step 2: Insert duplicate rows (rn>1) into rejects
INSERT INTO rejects (event_id, reason)
SELECT DISTINCT event_id, 'duplicate_event' as reason
FROM (
  SELECT 
    *,
    ROW_NUMBER() OVER (PARTITION BY event_id ORDER BY ROWID) as rn
  FROM fixture_seed
) t
WHERE rn > 1;

-- Step 3: Upsert checkpoint log with count from fixture_seed
INSERT INTO checkpoint_log (checkpoint_tag, row_count)
VALUES ('CKP-APR-01', (SELECT COUNT(*) FROM fixture_seed))
ON CONFLICT(checkpoint_tag) DO UPDATE SET row_count = excluded.row_count;

-- Commit transaction
COMMIT;

-- Verification: Display ledger
SELECT 'LEDGER:' as section;
SELECT * FROM ledger ORDER BY event_id;

-- Verification: Display rejects
SELECT 'REJECTS:' as section;
SELECT * FROM rejects ORDER BY event_id;

-- Verification: Display checkpoint_log
SELECT 'CHECKPOINT_LOG:' as section;
SELECT * FROM checkpoint_log;

-- Step 4: Return deterministic aggregate output
SELECT 'AGGREGATES:' as section;
SELECT category, SUM(amount) AS total FROM ledger GROUP BY category ORDER BY category;
