BEGIN TRANSACTION;

-- Step 1: Insert first-seen rows into ledger using ROW_NUMBER()
INSERT INTO ledger (event_id, category, amount, batch_id, checkpoint_tag)
SELECT 
  fs.event_id,
  fs.category,
  fs.amount,
  fs.batch_id,
  'CKP-APR-01' AS checkpoint_tag
FROM (
  SELECT 
    event_id,
    category,
    amount,
    batch_id,
    ROW_NUMBER() OVER (PARTITION BY event_id ORDER BY ROWID) AS rn
  FROM fixture_seed
) fs
WHERE fs.rn = 1;

-- Step 2: Insert duplicate rows into rejects
INSERT INTO rejects (event_id, reason)
SELECT 
  fs.event_id,
  'duplicate_event' AS reason
FROM (
  SELECT 
    event_id,
    ROW_NUMBER() OVER (PARTITION BY event_id ORDER BY ROWID) AS rn
  FROM fixture_seed
) fs
WHERE fs.rn > 1;

-- Step 3: Upsert checkpoint metadata with total row count from fixture_seed
INSERT INTO checkpoint_log (checkpoint_tag, row_count)
VALUES ('CKP-APR-01', (SELECT COUNT(*) FROM fixture_seed))
ON CONFLICT(checkpoint_tag) DO UPDATE SET row_count = excluded.row_count;

COMMIT;

-- Verify results
SELECT 'Ledger:' AS section;
SELECT * FROM ledger ORDER BY event_id;

SELECT 'Rejects:' AS section;
SELECT * FROM rejects ORDER BY event_id;

SELECT 'Checkpoint Log:' AS section;
SELECT * FROM checkpoint_log;

SELECT 'Aggregates by Category:' AS section;
SELECT category, SUM(amount) AS total FROM ledger GROUP BY category ORDER BY category;

-- Update checkpoint_log to reflect actual ledger row count
UPDATE checkpoint_log 
SET row_count = (SELECT COUNT(*) FROM ledger)
WHERE checkpoint_tag = 'CKP-APR-01';

-- Verify the correction
SELECT * FROM checkpoint_log;
