DROP TABLE IF EXISTS ledger;
DROP TABLE IF EXISTS rejects;
DROP TABLE IF EXISTS checkpoint_log;

CREATE TABLE ledger (
  event_id TEXT PRIMARY KEY,
  category TEXT NOT NULL,
  amount INTEGER NOT NULL,
  batch_id TEXT NOT NULL,
  checkpoint_tag TEXT NOT NULL
);

CREATE TABLE rejects (
  event_id TEXT NOT NULL,
  reason TEXT NOT NULL
);

CREATE TABLE checkpoint_log (
  checkpoint_tag TEXT PRIMARY KEY,
  row_count INTEGER NOT NULL
);

BEGIN TRANSACTION;

-- Step 1: Insert first-seen rows into ledger (using INSERT OR IGNORE for deduplication)
INSERT OR IGNORE INTO ledger (event_id, category, amount, batch_id, checkpoint_tag)
SELECT event_id, category, amount, batch_id, 'CKP-APR-01'
FROM fixture_seed;

-- Step 2: Identify and insert duplicate rows into rejects
-- Use ROW_NUMBER to identify duplicates
INSERT INTO rejects (event_id, reason)
SELECT event_id, 'duplicate_event'
FROM (
  SELECT event_id, ROW_NUMBER() OVER (PARTITION BY event_id ORDER BY rowid) AS rn
  FROM fixture_seed
) t
WHERE rn > 1;

-- Step 3: Upsert checkpoint_log with unique row count from ledger
INSERT OR REPLACE INTO checkpoint_log (checkpoint_tag, row_count)
SELECT 'CKP-APR-01', COUNT(DISTINCT event_id)
FROM fixture_seed;

COMMIT;

-- Verify ledger contents
SELECT 'LEDGER:' AS section;
SELECT * FROM ledger ORDER BY event_id;

-- Verify rejects contents
SELECT 'REJECTS:' AS section;
SELECT * FROM rejects;

-- Verify checkpoint_log
SELECT 'CHECKPOINT_LOG:' AS section;
SELECT * FROM checkpoint_log;

-- Return aggregate totals by category
SELECT 'AGGREGATES:' AS section;
SELECT category, SUM(amount) AS total FROM ledger GROUP BY category ORDER BY category;

-- Check the actual insert statement in the ledger table
SELECT COUNT(*) as ledger_rows, COUNT(DISTINCT event_id) as unique_events FROM ledger;
SELECT COUNT(*) as reject_rows FROM rejects;

