tool_input.sql:
-- Initialize tables from bootstrap
DROP TABLE IF EXISTS ledger;
DROP TABLE IF EXISTS rejects;
DROP TABLE IF EXISTS checkpoint_log;

CREATE TABLE ledger (
  event_id TEXT PRIMARY KEY,
  category TEXT NOT NULL,
  amount INTEGER NOT NULL,
  batch_id TEXT NOT NULL,
  checkpoint_tag TEXT NOT NULL
);

CREATE TABLE rejects (
  event_id TEXT NOT NULL,
  reason TEXT NOT NULL
);

CREATE TABLE checkpoint_log (
  checkpoint_tag TEXT PRIMARY KEY,
  row_count INTEGER NOT NULL
);

-- Create fixture_seed from the CSV data
CREATE TABLE fixture_seed (
  event_id TEXT,
  category TEXT,
  amount INTEGER,
  batch_id TEXT
);

INSERT INTO fixture_seed (event_id, category, amount, batch_id) VALUES
('e1', 'drums', 5, 'b1'),
('e2', 'bass', 4, 'b1'),
('e3', 'lead', 3, 'b1'),
('e2', 'bass', 4, 'b1'),
('e4', 'drums', 8, 'b1');

-- BEGIN TRANSACTION for reconciliation workflow
BEGIN TRANSACTION;

-- Step 1: Identify unique (first-seen) rows by event_id
-- Insert them into ledger with checkpoint tag CKP-APR-01
INSERT INTO ledger (event_id, category, amount, batch_id, checkpoint_tag)
SELECT DISTINCT ON (event_id) event_id, category, amount, batch_id, 'CKP-APR-01'
FROM fixture_seed
ORDER BY event_id;

-- Step 2: Identify and insert duplicate rows into rejects
INSERT INTO rejects (event_id, reason)
SELECT f.event_id, 'duplicate_event'
FROM fixture_seed f
WHERE f.event_id IN (
  SELECT event_id FROM fixture_seed GROUP BY event_id HAVING COUNT(*) > 1
)
AND (f.event_id, f.category, f.amount, f.batch_id) NOT IN (
  SELECT event_id, category, amount, batch_id FROM ledger
);

-- Step 3: Count inserted rows and upsert checkpoint_log
INSERT INTO checkpoint_log (checkpoint_tag, row_count)
SELECT 'CKP-APR-01', COUNT(*)
FROM ledger
WHERE checkpoint_tag = 'CKP-APR-01'
ON CONFLICT(checkpoint_tag) DO UPDATE SET row_count = excluded.row_count;

-- COMMIT transaction
COMMIT;

-- Verify aggregate output with deterministic ordering
SELECT category, SUM(amount) AS total
FROM ledger
GROUP BY category
ORDER BY category;

-- Verify ledger contents
SELECT 'LEDGER' as table_name;
SELECT * FROM ledger ORDER BY event_id;

-- Verify rejects contents
SELECT 'REJECTS' as table_name;
SELECT * FROM rejects ORDER BY event_id;

-- Verify checkpoint_log
SELECT 'CHECKPOINT_LOG' as table_name;
SELECT * FROM checkpoint_log;

-- Final aggregate (deterministic)
SELECT 'FINAL AGGREGATE' as result_type;
SELECT category, SUM(amount) AS total
FROM ledger
GROUP BY category
ORDER BY category;
