DROP TABLE IF EXISTS ledger;
DROP TABLE IF EXISTS rejects;
DROP TABLE IF EXISTS checkpoint_log;

CREATE TABLE ledger (
  event_id TEXT PRIMARY KEY,
  category TEXT NOT NULL,
  amount INTEGER NOT NULL,
  batch_id TEXT NOT NULL,
  checkpoint_tag TEXT NOT NULL
);

CREATE TABLE rejects (
  event_id TEXT NOT NULL,
  reason TEXT NOT NULL
);

CREATE TABLE checkpoint_log (
  checkpoint_tag TEXT PRIMARY KEY,
  row_count INTEGER NOT NULL
);

BEGIN TRANSACTION;

-- Insert first-seen rows into ledger
INSERT INTO ledger (event_id, category, amount, batch_id, checkpoint_tag)
SELECT DISTINCT ON (event_id) event_id, category, amount, batch_id, 'CKP-APR-01'
FROM fixture_seed
ORDER BY event_id;

-- Identify and insert duplicates into rejects
INSERT INTO rejects (event_id, reason)
SELECT event_id, 'duplicate_event'
FROM fixture_seed
WHERE event_id IN (
  SELECT event_id FROM fixture_seed GROUP BY event_id HAVING COUNT(*) > 1
)
AND event_id NOT IN (
  SELECT event_id FROM ledger
);

-- Count inserted rows and upsert checkpoint log
INSERT INTO checkpoint_log (checkpoint_tag, row_count)
VALUES ('CKP-APR-01', (SELECT COUNT(*) FROM ledger WHERE checkpoint_tag = 'CKP-APR-01'))
ON CONFLICT(checkpoint_tag) DO UPDATE SET row_count = excluded.row_count;

COMMIT;

BEGIN TRANSACTION;

-- Insert first-seen rows into ledger (using ROW_NUMBER for deduplication)
INSERT INTO ledger (event_id, category, amount, batch_id, checkpoint_tag)
SELECT event_id, category, amount, batch_id, 'CKP-APR-01'
FROM (
  SELECT event_id, category, amount, batch_id, ROW_NUMBER() OVER (PARTITION BY event_id ORDER BY rowid) as rn
  FROM fixture_seed
)
WHERE rn = 1;

-- Identify and insert duplicates into rejects
INSERT INTO rejects (event_id, reason)
SELECT event_id, 'duplicate_event'
FROM (
  SELECT event_id, ROW_NUMBER() OVER (PARTITION BY event_id ORDER BY rowid) as rn
  FROM fixture_seed
)
WHERE rn > 1;

-- Count inserted rows and upsert checkpoint log
INSERT INTO checkpoint_log (checkpoint_tag, row_count)
VALUES ('CKP-APR-01', (SELECT COUNT(*) FROM ledger WHERE checkpoint_tag = 'CKP-APR-01'))
ON CONFLICT(checkpoint_tag) DO UPDATE SET row_count = excluded.row_count;

COMMIT;

