-- Initialize tables from bootstrap
DROP TABLE IF EXISTS ledger;
DROP TABLE IF EXISTS rejects;
DROP TABLE IF EXISTS checkpoint_log;

CREATE TABLE ledger (
  event_id TEXT PRIMARY KEY,
  category TEXT NOT NULL,
  amount INTEGER NOT NULL,
  batch_id TEXT NOT NULL,
  checkpoint_tag TEXT NOT NULL
);

CREATE TABLE rejects (
  event_id TEXT NOT NULL,
  reason TEXT NOT NULL
);

CREATE TABLE checkpoint_log (
  checkpoint_tag TEXT PRIMARY KEY,
  row_count INTEGER NOT NULL
);

-- Verify fixture_seed exists and check its content
SELECT * FROM fixture_seed LIMIT 5;

-- Verify ledger contents
SELECT 'LEDGER:' AS section;
SELECT * FROM ledger ORDER BY event_id;

SELECT '' AS section;
SELECT 'REJECTS:' AS section;
SELECT * FROM rejects ORDER BY event_id;

SELECT '' AS section;
SELECT 'CHECKPOINT_LOG:' AS section;
SELECT * FROM checkpoint_log;

SELECT '' AS section;
SELECT 'AGGREGATE TOTALS:' AS section;
SELECT category, SUM(amount) AS total FROM ledger GROUP BY category ORDER BY category;

BEGIN TRANSACTION;

-- Clear rejects and reinsert with deduplication
DELETE FROM rejects;

-- Insert duplicates into rejects (one entry per duplicate event_id)
INSERT INTO rejects (event_id, reason)
SELECT DISTINCT event_id, 'duplicate_event'
FROM (
  SELECT event_id, ROW_NUMBER() OVER (PARTITION BY event_id ORDER BY rowid) AS rn
  FROM fixture_seed
)
WHERE rn > 1;

COMMIT;

SELECT 'REJECTS (corrected):' AS section;
SELECT * FROM rejects ORDER BY event_id;

SELECT '' AS section;
SELECT 'Reject count check:' AS section;
SELECT COUNT(*) FROM rejects WHERE reason = 'duplicate_event';

-- Reset and start fresh in a single clean workflow
DROP TABLE IF EXISTS ledger;
DROP TABLE IF EXISTS rejects;
DROP TABLE IF EXISTS checkpoint_log;

CREATE TABLE ledger (
  event_id TEXT PRIMARY KEY,
  category TEXT NOT NULL,
  amount INTEGER NOT NULL,
  batch_id TEXT NOT NULL,
  checkpoint_tag TEXT NOT NULL
);

CREATE TABLE rejects (
  event_id TEXT NOT NULL,
  reason TEXT NOT NULL
);

CREATE TABLE checkpoint_log (
  checkpoint_tag TEXT PRIMARY KEY,
  row_count INTEGER NOT NULL
);

BEGIN TRANSACTION;

-- Step 1: Insert first-seen rows into ledger
INSERT INTO ledger (event_id, category, amount, batch_id, checkpoint_tag)
SELECT event_id, category, amount, batch_id, 'CKP-APR-01'
FROM (
  SELECT event_id, category, amount, batch_id, ROW_NUMBER() OVER (PARTITION BY event_id ORDER BY rowid) AS rn
  FROM fixture_seed
)
WHERE rn = 1;

-- Step 2: Insert duplicates into rejects (one per duplicate event_id)
INSERT INTO rejects (event_id, reason)
SELECT DISTINCT event_id, 'duplicate_event'
FROM (
  SELECT event_id, ROW_NUMBER() OVER (PARTITION BY event_id ORDER BY rowid) AS rn
  FROM fixture_seed
)
WHERE rn > 1;

-- Step 3: Upsert checkpoint_log with row count
INSERT INTO checkpoint_log (checkpoint_tag, row_count)
SELECT 'CKP-APR-01', COUNT(*)
FROM ledger
WHERE checkpoint_tag = 'CKP-APR-01'
ON CONFLICT(checkpoint_tag) DO UPDATE SET
  row_count = excluded.row_count;

COMMIT;

SELECT 'FINAL VERIFICATION:' AS section;
SELECT '--- LEDGER ---' AS header;
SELECT * FROM ledger ORDER BY event_id;

SELECT '--- REJECTS ---' AS header;
SELECT * FROM rejects ORDER BY event_id;

SELECT '--- CHECKPOINT_LOG ---' AS header;
SELECT * FROM checkpoint_log;

SELECT '--- AGGREGATE TOTALS ---' AS header;
SELECT category, SUM(amount) AS total FROM ledger GROUP BY category ORDER BY category;
