-- Bootstrap schema
DROP TABLE IF EXISTS ledger;
DROP TABLE IF EXISTS rejects;
DROP TABLE IF EXISTS checkpoint_log;

CREATE TABLE ledger (
  event_id TEXT PRIMARY KEY,
  category TEXT NOT NULL,
  amount INTEGER NOT NULL,
  batch_id TEXT NOT NULL,
  checkpoint_tag TEXT NOT NULL
);

CREATE TABLE rejects (
  event_id TEXT NOT NULL,
  reason TEXT NOT NULL
);

CREATE TABLE checkpoint_log (
  checkpoint_tag TEXT PRIMARY KEY,
  row_count INTEGER NOT NULL
);

-- Load fixture data into temporary table
CREATE TEMPORARY TABLE fixture_seed (
  event_id TEXT,
  category TEXT,
  amount INTEGER,
  batch_id TEXT
);

INSERT INTO fixture_seed VALUES
  ('e1', 'drums', 5, 'b1'),
  ('e2', 'bass', 4, 'b1'),
  ('e3', 'lead', 3, 'b1'),
  ('e2', 'bass', 4, 'b1'),
  ('e4', 'drums', 8, 'b1');

-- Begin transaction for atomic ingest
BEGIN TRANSACTION;

-- Insert first-seen rows into ledger
INSERT INTO ledger (event_id, category, amount, batch_id, checkpoint_tag)
SELECT DISTINCT ON (event_id) event_id, category, amount, batch_id, 'CKP-APR-01'
FROM fixture_seed
WHERE event_id NOT IN (SELECT event_id FROM ledger)
ORDER BY event_id;

-- Identify duplicates and insert into rejects
INSERT INTO rejects (event_id, reason)
SELECT event_id, 'duplicate_event'
FROM fixture_seed
WHERE event_id IN (
  SELECT event_id FROM fixture_seed GROUP BY event_id HAVING COUNT(*) > 1
) AND event_id NOT IN (
  SELECT event_id FROM rejects WHERE reason = 'duplicate_event'
);

-- Upsert checkpoint log
INSERT INTO checkpoint_log (checkpoint_tag, row_count)
SELECT 'CKP-APR-01', COUNT(*)
FROM ledger
WHERE checkpoint_tag = 'CKP-APR-01'
ON CONFLICT(checkpoint_tag) DO UPDATE SET row_count = excluded.row_count;

COMMIT;

-- Verify aggregate output
SELECT category, SUM(amount) AS total 
FROM ledger 
GROUP BY category 
ORDER BY category;

-- Bootstrap schema
DROP TABLE IF EXISTS ledger;
DROP TABLE IF EXISTS rejects;
DROP TABLE IF EXISTS checkpoint_log;

CREATE TABLE ledger (
  event_id TEXT PRIMARY KEY,
  category TEXT NOT NULL,
  amount INTEGER NOT NULL,
  batch_id TEXT NOT NULL,
  checkpoint_tag TEXT NOT NULL
);

CREATE TABLE rejects (
  event_id TEXT NOT NULL,
  reason TEXT NOT NULL
);

CREATE TABLE checkpoint_log (
  checkpoint_tag TEXT PRIMARY KEY,
  row_count INTEGER NOT NULL
);

-- Verify ledger contents
SELECT 'LEDGER' as table_name, * FROM ledger
ORDER BY event_id;

-- Verify rejects contents
SELECT 'REJECTS' as table_name, * FROM rejects
ORDER BY event_id;
