-- Bootstrap schema
DROP TABLE IF EXISTS ledger;
DROP TABLE IF EXISTS rejects;
DROP TABLE IF EXISTS checkpoint_log;

CREATE TABLE ledger (
  event_id TEXT PRIMARY KEY,
  category TEXT NOT NULL,
  amount INTEGER NOT NULL,
  batch_id TEXT NOT NULL,
  checkpoint_tag TEXT NOT NULL
);

CREATE TABLE rejects (
  event_id TEXT NOT NULL,
  reason TEXT NOT NULL
);

CREATE TABLE checkpoint_log (
  checkpoint_tag TEXT PRIMARY KEY,
  row_count INTEGER NOT NULL
);

-- Create fixture_seed with the CSV data
CREATE TABLE fixture_seed (
  event_id TEXT NOT NULL,
  category TEXT NOT NULL,
  amount INTEGER NOT NULL,
  batch_id TEXT NOT NULL
);

INSERT INTO fixture_seed (event_id, category, amount, batch_id) VALUES
  ('e1', 'drums', 5, 'b1'),
  ('e2', 'bass', 4, 'b1'),
  ('e3', 'lead', 3, 'b1'),
  ('e2', 'bass', 4, 'b1'),
  ('e4', 'drums', 8, 'b1');

SELECT * FROM fixture_seed;

-- Bootstrap schema
DROP TABLE IF EXISTS ledger;
DROP TABLE IF EXISTS rejects;
DROP TABLE IF EXISTS checkpoint_log;

CREATE TABLE ledger (
  event_id TEXT PRIMARY KEY,
  category TEXT NOT NULL,
  amount INTEGER NOT NULL,
  batch_id TEXT NOT NULL,
  checkpoint_tag TEXT NOT NULL
);

CREATE TABLE rejects (
  event_id TEXT NOT NULL,
  reason TEXT NOT NULL
);

CREATE TABLE checkpoint_log (
  checkpoint_tag TEXT PRIMARY KEY,
  row_count INTEGER NOT NULL
);

-- Verify schema created
SELECT name FROM sqlite_master WHERE type='table' ORDER BY name;

BEGIN TRANSACTION;

-- Step 1: Insert unique rows (first occurrence) into ledger
INSERT INTO ledger (event_id, category, amount, batch_id, checkpoint_tag)
SELECT DISTINCT ON (event_id) event_id, category, amount, batch_id, 'CKP-APR-01'
FROM fixture_seed
ORDER BY event_id, ROWID;

-- Step 2: Identify and insert duplicates into rejects
INSERT INTO rejects (event_id, reason)
SELECT event_id, 'duplicate_event'
FROM fixture_seed
WHERE event_id IN (
  SELECT event_id
  FROM fixture_seed
  GROUP BY event_id
  HAVING COUNT(*) > 1
)
EXCEPT
SELECT event_id, 'duplicate_event'
FROM (
  SELECT DISTINCT ON (event_id) event_id
  FROM fixture_seed
  ORDER BY event_id, ROWID
);

-- Step 3: Count inserted rows and upsert checkpoint log
INSERT INTO checkpoint_log (checkpoint_tag, row_count)
SELECT 'CKP-APR-01', COUNT(*)
FROM ledger
WHERE checkpoint_tag = 'CKP-APR-01'
ON CONFLICT(checkpoint_tag) DO UPDATE SET row_count = excluded.row_count;

COMMIT;

-- Verify results
SELECT 'LEDGER CONTENTS:' AS section;
SELECT * FROM ledger ORDER BY event_id;

SELECT 'REJECTS CONTENTS:' AS section;
SELECT * FROM rejects ORDER BY event_id;

SELECT 'CHECKPOINT LOG:' AS section;
SELECT * FROM checkpoint_log;

SELECT 'AGGREGATE TOTALS:' AS section;
SELECT category, SUM(amount) AS total FROM ledger GROUP BY category ORDER BY category;

BEGIN TRANSACTION;

-- Step 1: Insert first-seen rows into ledger using row number to identify unique occurrences
INSERT INTO ledger (event_id, category, amount, batch_id, checkpoint_tag)
SELECT 
  event_id, 
  category, 
  amount, 
  batch_id, 
  'CKP-APR-01'
FROM (
  SELECT 
    event_id,
    category,
    amount,
    batch_id,
    ROW_NUMBER() OVER (PARTITION BY event_id ORDER BY ROWID) AS rn
  FROM fixture_seed
) t
WHERE rn = 1;

-- Step 2: Insert duplicate occurrences into rejects
INSERT INTO rejects (event_id, reason)
SELECT 
  event_id,
  'duplicate_event'
FROM (
  SELECT 
    event_id,
    ROW_NUMBER() OVER (PARTITION BY event_id ORDER BY ROWID) AS rn
  FROM fixture_seed
) t
WHERE rn > 1;

-- Step 3: Upsert checkpoint log
INSERT INTO checkpoint_log (checkpoint_tag, row_count)
SELECT 'CKP-APR-01', COUNT(*)
FROM ledger
WHERE checkpoint_tag = 'CKP-APR-01'
ON CONFLICT(checkpoint_tag) DO UPDATE SET row_count = excluded.row_count;

COMMIT;

-- Verify results
SELECT 'LEDGER CONTENTS:' AS section;
SELECT * FROM ledger ORDER BY event_id;

SELECT 'REJECTS CONTENTS:' AS section;
SELECT * FROM rejects ORDER BY event_id;

SELECT 'CHECKPOINT LOG:' AS section;
SELECT * FROM checkpoint_log;

SELECT 'AGGREGATE TOTALS:' AS section;
SELECT category, SUM(amount) AS total FROM ledger GROUP BY category ORDER BY category;

-- Final verification of state
SELECT 'TABLE SCHEMAS:' AS info;
SELECT sql FROM sqlite_master WHERE type='table' AND name IN ('ledger', 'rejects', 'checkpoint_log');

SELECT 'FINAL LEDGER STATE:' AS info;
SELECT COUNT(*) as ledger_row_count FROM ledger;
SELECT COUNT(*) as reject_row_count FROM rejects;
SELECT COUNT(*) as checkpoint_row_count FROM checkpoint_log;

-- Verify deterministic aggregate output
SELECT category, SUM(amount) AS total FROM ledger GROUP BY category ORDER BY category;

-- Clear all tables to start fresh
DELETE FROM checkpoint_log;
DELETE FROM rejects;
DELETE FROM ledger;

-- Verify clean state
SELECT COUNT(*) as ledger_count FROM ledger;
SELECT COUNT(*) as rejects_count FROM rejects;
SELECT COUNT(*) as checkpoint_count FROM checkpoint_log;
