BEGIN TRANSACTION;
INSERT INTO ledger (event_id, category, amount, batch_id, checkpoint_tag) SELECT event_id, category, amount, batch_id, 'CKP-APR-01' FROM (SELECT event_id, category, amount, batch_id, ROW_NUMBER() OVER (PARTITION BY event_id ORDER BY ROWID) AS rn FROM fixture_seed) ranked WHERE rn = 1;
INSERT INTO rejects (event_id, reason) SELECT DISTINCT event_id, 'duplicate_event' FROM (SELECT event_id, ROW_NUMBER() OVER (PARTITION BY event_id ORDER BY ROWID) AS rn FROM fixture_seed) ranked WHERE rn > 1;
INSERT INTO checkpoint_log (checkpoint_tag, row_count) VALUES ('CKP-APR-01', (SELECT COUNT(*) FROM ledger WHERE checkpoint_tag = 'CKP-APR-01')) ON CONFLICT(checkpoint_tag) DO UPDATE SET row_count = (SELECT COUNT(*) FROM ledger WHERE checkpoint_tag = 'CKP-APR-01');
COMMIT;
