BEGIN TRANSACTION;

-- Clear previous results
DELETE FROM ledger;
DELETE FROM rejects;
DELETE FROM checkpoint_log;

-- Step 1: Insert all first-occurrence rows into ledger
-- Use rowid to identify first occurrence
INSERT INTO ledger (event_id, category, amount, batch_id, checkpoint_tag)
SELECT fs.event_id, fs.category, fs.amount, fs.batch_id, 'CKP-APR-01'
FROM (
  SELECT event_id, category, amount, batch_id, 
         ROW_NUMBER() OVER (PARTITION BY event_id ORDER BY rowid) as rn
  FROM fixture_seed
) fs
WHERE fs.rn = 1;

-- Step 2: Insert duplicate rows into rejects (those with rn > 1)
INSERT INTO rejects (event_id, reason)
SELECT DISTINCT fs.event_id, 'duplicate_event'
FROM (
  SELECT event_id,
         ROW_NUMBER() OVER (PARTITION BY event_id ORDER BY rowid) as rn
  FROM fixture_seed
) fs
WHERE fs.rn > 1;

-- Step 3: Insert checkpoint metadata
INSERT INTO checkpoint_log (checkpoint_tag, row_count)
VALUES ('CKP-APR-01', (
  SELECT COUNT(DISTINCT event_id) FROM fixture_seed
  WHERE event_id IN (
    SELECT event_id FROM fixture_seed
    GROUP BY event_id
    HAVING COUNT(*) = 1
  )
  UNION ALL
  SELECT COUNT(DISTINCT event_id) FROM fixture_seed
  WHERE event_id IN (
    SELECT event_id FROM fixture_seed
    GROUP BY event_id
    HAVING COUNT(*) > 1
  )
));

COMMIT;

BEGIN TRANSACTION;

-- Insert e2 (bass) which should have been included as the first occurrence
INSERT INTO ledger (event_id, category, amount, batch_id, checkpoint_tag)
SELECT event_id, category, amount, batch_id, 'CKP-APR-01'
FROM (
  SELECT event_id, category, amount, batch_id, 
         ROW_NUMBER() OVER (PARTITION BY event_id ORDER BY rowid) as rn
  FROM fixture_seed
) fs
WHERE fs.rn = 1
AND fs.event_id NOT IN (SELECT event_id FROM ledger);

-- Update rejects to ensure e2 is there as duplicate
INSERT INTO rejects (event_id, reason)
SELECT DISTINCT event_id, 'duplicate_event'
FROM (
  SELECT event_id,
         ROW_NUMBER() OVER (PARTITION BY event_id ORDER BY rowid) as rn
  FROM fixture_seed
) fs
WHERE fs.rn > 1
AND fs.event_id NOT IN (SELECT event_id FROM rejects);

-- Update checkpoint log with correct count (all unique first occurrences)
DELETE FROM checkpoint_log WHERE checkpoint_tag = 'CKP-APR-01';

INSERT INTO checkpoint_log (checkpoint_tag, row_count)
SELECT 'CKP-APR-01', COUNT(DISTINCT event_id) FROM fixture_seed;

COMMIT;
